import React from "react";

function Button({text}) {
  const handleClick = () => {
    this.anyRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' })
    };
  return (
    <button
        className="btn"
        onClick={handleClick}
      >
        {text}
    </button>
  );
}

export default Button;
