import React, { useState } from "react";

function Button({text}) {
  const [active, setActive] = useState(false);
  const handleClick = () => {
    setActive(!active);
  };

  return (
    <div className="center">
      <button
        className="btn"
        onClick={handleClick}
        style={{ backgroundColor: active ? "black" : "white" }}
      >
        {text}
      </button>
    </div>
  );
}

export default Button;
