import React, { useState } from "react";

function Button({text}) {
  const [active, setActive] = useState(false);
  const handleClick = () => {
    setActive(!active);
  };

  return (
    <button
        className="btn"
        onClick={handleClick}
        style={{ transition: active ? "color: black white 1s" : "color: white black 1s" }}
      >
        {text}
    </button>
  );
}

export default Button;
