import './App.css';
import Header from './components/Header';
import Body from './components/Body';
import Footer from './components/Footer';
import React, { useRef } from 'react';
import Button from './components/Button';


function App() {

  const scrollBody = useRef(null);
  const scrollFooter = useRef(null);

  return (
    <div className='App'>
      < header id='header'>
        <div className='navbar'>
            <Button text='About' onClick={() => scollToRef.current.scrollIntoView()}/>
            <Button text='Experience'/>
            <Button text='Contact'/>
        </div>
      </header>
      < Body id='body' ref={scrollBody}/>     
      < Footer id='footer' ref={scrollFooter}/>
    </div>
  );
}

export default App;
