import React from "react";

function Button({text, id}) {
    const scroll = () => {
        const section = document.querySelector( '#body' );
        section.scrollIntoView( { behavior: 'smooth'} );
      };
  return (
    <button
        className="btn"
        onClick={scroll}
      >
        {text}
    </button>
  );
}

export default Button;
