import lb from './img/layback.jpeg'
import React, {useEffect, useRef} from 'react';


const About = () => {
  const targetRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const element = targetRef.current;

      if (element) {
        const rect = element.getBoundingClientRect();
        const windowHeight = window.innerHeight;

        // Calculate the distance from the element's top and bottom to the viewport
        const topDistance = rect.top;
        const bottomDistance = windowHeight - rect.bottom;

        // Calculate the opacity based on the distance from the viewport
        let opacity = 1;

        if (topDistance < 0) {
          opacity = 1 - Math.abs(topDistance) / windowHeight;
        } else if (bottomDistance < 0) {
          opacity = 1.2 - Math.abs(bottomDistance) / windowHeight;
        }

        element.style.opacity = opacity > 0 ? opacity : 0;
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);


  return (
    <div
      className='content'
      id='about'
      style={{backgroundImage: `url(${lb})`}}
    >

    <div ref={targetRef} className='fade-in'>
      <div className='about-sub' id='intro'>
        <h2>
          About me
        </h2>
        <p>
          I'm a recent CS Graduate. I'm interested in all things
          digital. I like web development, high performance computing,
          and video games. This is my webpage that I made using React.
          As you can see it is pretty cool. I'm going to test things out
          and make this a great website.
        </p>
        <p>
          Outside of work and school, I'm interested in everything outdooors.
          I'm an active climber, cyclist, and backcountry skier. 
        </p>
      </div>

      <div
        className='about-sub'
        id='experience'>
        <h2>
          Experience
        </h2>

        <ul>
          <li>
            <b className='job'>Rapid Reboot -
              <i className='job-title'> Systems Engineer Intern</i>
            </b>
            <ul>
              <li>Software and hardware testing.</li>
              <li>Assembly and repair of medical devices.</li>
              <li>Designed and prototyped custom CMS.</li>
            </ul>
          </li>

          <li>
            <b style={{fontSize: '16px + 1vmin', color:'#CC6500'}}>Mountain West Wholesale -
              <i style={{fontSize: '10px + 1vmin'}}> Web Developer & General Manager</i>
            </b>
            <ul>
              <li>Software and hardware testing.</li>
              <li>Assembly and repair of medical devices.</li>
              <li>Designed and prototyped custom CMS.</li>
            </ul>
          </li>

          <li>
            <b style={{fontSize: '16px + 1vmin'}}>Rapid Reboot -
              <b> Systems Engineer Intern</b>
            </b>
            <ul>
              <li>Software and hardware testing.</li>
              <li>Assembly and repair of medical devices.</li>
              <li>Designed and prototyped custom CMS.</li>
            </ul>
          </li>
        </ul>
      </div>


    </div>
    </div>
  )
}

export default About
